value.value:
{
    "@type": "MessageCard",
    "@context": "http://schema.org/extensions",
    "themeColor": "0076D7",
    "summary": "Pipeline status alert message​​​​",
    "sections": [
        {
            "activityTitle": "Pipeline alert​​​​",
            "facts": [
                {
                    "name": "Pipeline RunId:",
                    "value": "@{pipeline().parameters.pipelineRunId}"
                },
                {
                    "name": "Activity name:",
                    "value": "@{pipeline().parameters.activityName}"
                },
                {
                    "name": "Activity status:",
                    "value": "@{pipeline().parameters.activityStatus}"
                },
                {
                    "name": "Execution duration (s):",
                    "value": "@{pipeline().parameters.activityDuration}"
                },
                {
                    "name": "Message",
                    "value": "@{pipeline().parameters.activityMessage}"
                },
                {
                    "name": "Notification time (UTC):",
                    "value": "@{utcNow()}"
                },
                {
                    "name": "Data Factory name:",
                    "value": "@{pipeline().DataFactory}"
                }
            ],
            "markdown": true
        }
    ],
    "potentialAction": [
        {
            "@type": "OpenUri",
            "name": "View pipeline run",
            "targets": [
                {
                    "os": "default",
                    "uri": "@{concat('https://adf.azure.com/monitoring/pipelineruns/',pipeline().parameters.pipelineRunId,'?factory=/subscriptions/',pipeline().parameters.dataFactorySubscription,'/resourceGroups/',pipeline().parameters.dataFactoryResourceGroup,'/providers/Microsoft.DataFactory/factories/',pipeline().DataFactory)}"
                }
            ]
        }
    ]
}
