# Calculate Betweeness Index by counting the number of time each node appears on shortest paths between sampled src->dst nodes

# parameters for tuning the cost of betweeness calculation
dstNodesLimit = 1 # num of destination nodes per source node to sample for calculating shortest paths
bfsPathLimit = 5 # max shortest path length

import random
import copy
nodes = [v.id for v in vertices.collect()]
betweenessCounts = {}

for src in nodes:
    betweenessCounts[src] = 0

for src in nodes:
    dstNodesCount = 0
    dstNodes = copy.deepcopy(nodes)
    random.shuffle(dstNodes)

    for dst in dstNodes:

        if (src == dst):
            continue

        if (result.count() > 0):
            resultVertices = list(filter(lambda x: x.startswith('v'), result.columns))

            for v in resultVertices:
                vIds = [row[v]["id"] for row in result.select(col(v)).collect()]
                for vId in vIds:
                    if vId in betweenessCounts:
                        betweenessCounts[vId] += 1
                    else:
                        betweenessCounts[vId] = 1

        dstNodesCount += 1
        if (dstNodesCount >= dstNodesLimit):
            break

# Normalize betweeness index by total number of shortest paths sampled
numShortestPaths = dstNodesLimit * len(nodes)
def getBetweenessIndex(x):
    return betweenessCounts.get(x) / float(numShortestPaths)
